// plugins/agent.ts
async function elizaAgent(fastify, opts) {
    const agents = new Map();
    
    fastify.decorate('agentRuntime', {
        async createAgent(character) {
            const runtime = new AgentRuntime({
                databaseAdapter: fastify.db,
                modelProvider: character.modelProvider,
                character,
                plugins: [], // Empty initially - populated by discovery
                providers: [],
                actions: [],
                services: []
            });
            agents.set(character.id, runtime);
            return runtime;
        }
    });

    // Plugin discovery and registration
    fastify.addHook('onReady', async () => {
        await discoverAndLoadPlugins(fastify);
    });
}
