class JobSelector {
    async selectOptimalJobs(continuations: MessageContinuation[]) {
        const sorted = continuations.sort((a, b) => {
            const aRoi = a.requirements.estimatedReward / a.requirements.estimatedCost;
            const bRoi = b.requirements.estimatedReward / b.requirements.estimatedCost;
            return bRoi - aRoi;
        });

        const selected = [];
        let remainingCapacity = this.getTotalCapacity();
        
        for (const job of sorted) {
            if (this.canFit(job, remainingCapacity)) {
                selected.push(job);
                remainingCapacity = this.subtractResources(remainingCapacity, job.requirements);
            }
        }
        
        return selected;
    }
}
