class ContinuationProcessor {
    async process(continuation: MessageContinuation) {
        // Record starting state
        const startState = await this.captureState();
        
        try {
            // Execute continuation
            const result = await continuation.nextStep.continuation();
            
            // Create next continuation
            return {
                ...continuation,
                previousSteps: [...continuation.previousSteps, {
                    type: continuation.nextStep.type,
                    result
                }],
                nextStep: await this.determineNextStep(result)
            };
        } catch (error) {
            // Rollback to starting state if needed
            await this.rollbackTo(startState);
            throw error;
        }
    }
}
