class ContinuationFactory {
    createMetaContinuation<T>(initialValue: T): MetaContinuation<T> {
        const self = {
            value: initialValue,
            metaState: {
                depth: 0,
                history: []
            },
            continue: async () => {
                // Each continuation creates a new meta-continuation
                const nextValue = await this.computeNextValue(self.value);
                return this.createMetaContinuation(nextValue);
            }
        };
        return self;
    }

    async compose<T>(...continuations: MetaContinuation<T>[]): Promise<MetaContinuation<T>> {
        return {
            value: continuations[0].value,
            metaState: {
                depth: Math.max(...continuations.map(c => c.metaState.depth)) + 1,
                history: continuations.flatMap(c => c.metaState.history)
            },
            continue: async () => {
                // Compose multiple continuations into a single meta-continuation
                const results = await Promise.all(
                    continuations.map(c => c.continue())
                );
                return this.compose(...results);
            }
        };
    }
}
