class MetaContinuationProcessor {
    async process<T>(continuation: MetaContinuation<T>, depth: number = 0): Promise<T> {
        if (depth > 3) {
            throw new Error('Meta-continuation depth limit exceeded');
        }

        const result = await continuation.continue();
        
        // The meta part: decide whether to continue the continuation
        if (this.shouldContinueProcessing(result)) {
            return this.process(result, depth + 1);
        }

        return result.value;
    }

    private shouldContinueProcessing<T>(continuation: MetaContinuation<T>): boolean {
        return continuation.metaState.depth < this.maxDepth 
            && this.hasUnresolvedDependencies(continuation);
    }
}
